import { useState, useEffect } from "react";
import { useRouter } from "next/router";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Spinner from "react-bootstrap/Spinner";
import Article from "../../components/Blog/Article/Article";

const Post = () => {
  const { slug } = useRouter().query;
  const [post, setPost] = useState({
    requestComplete: false,
    data: null,
  });

  useEffect(() => {
    fetch(`${process.env.WP_API}/posts?slug=${slug}&_embed`)
      .then((res) => res.json())
      .then((data) => {
        data = data[0];
        setPost({ requestComplete: true, data });
      })
      .catch(() => setPost({ requestComplete: true }));
  }, [slug]);

  let content = null;
  if (!post.requestComplete) {
    content = (
      <div className="text-center">
        <Spinner
          className="mx-auto"
          variant="primary"
          role="status"
          animation="border"
        />
      </div>
    );
  } else if (post.data) {
    content = (
      <Container>
        <Col className="post mx-auto">
          <a href="/blog">Back to posts</a>
          <hr />
          <Article post={post} />
        </Col>
      </Container>
    );
  }

  return content ? content : "Sorry... couldn't find what you were looking for";
};

export default Post;
