import { useState, useEffect } from "react";
import { useRouter } from "next/router";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Spinner from "react-bootstrap/Spinner";
import Article from "../../components/Blog/Article/Article";

const Post = ({ post }) => {
  // const { slug } = useRouter().query;
  // const [post, setPost] = useState({
  //   requestComplete: false,
  //   data: null,
  // });

  // useEffect(() => {
  //   fetch(`${process.env.WP_API}/posts?slug=${slug}&_embed`)
  //     .then((res) => res.json())
  //     .then((data) => {
  //       data = data[0];
  //       setPost({ requestComplete: true, data });
  //     })
  //     .catch(() => setPost({ requestComplete: true }));
  // }, [slug]);

  let content = null;
  if (!post.requestComplete) {
    content = (
      <div className="text-center">
        <Spinner
          className="mx-auto"
          variant="primary"
          role="status"
          animation="border"
        />
      </div>
    );
  } else if (post.data) {
    content = (
      <Container>
        <Col className="post mx-auto">
          <a href="/blog">Back to posts</a>
          <hr />
          <Article post={post} />
        </Col>
      </Container>
    );
  }

  return content ? content : "Sorry... couldn't find what you were looking for";
};

// This function gets called at build time
export async function getStaticPaths() {
  // Call an external API endpoint to get posts
  const res = await fetch(`${process.env.WP_API}/postss`);
  const posts = await res.json();

  // Get the paths we want to pre-render based on posts
  const paths = posts.map((post) => ({
    params: { slug: post.slug },
  }));

  // We'll pre-render only these paths at build time.
  // { fallback: false } means other routes should 404.
  return { paths, fallback: false };
}

// This also gets called at build time
export async function getStaticProps({ params }) {
  const res = await fetch(
    `${process.env.WP_API}/posts?slug=${params.slug}&_embed`
  );
  const post = await res.json()[0];

  // Pass post data to the page via props
  return { props: { post } };
}

export default Post;
